import React from 'react';
import { deleteGuest } from '../HotelService';

const Booking = ({ guest ,removeGuest }) => {
    console.log(removeGuest)
    const handleDelete = () => {
        removeGuest(guest._id).then(() => {
            deleteGuest(guest._id);
        });
    };

    return (
        <li>
            {guest.name}
            <div onClick={handleDelete}>Remove</div>
        </li>
    );
};

export default Booking;
